// Filtra clientes con procesar=SI cuyo hora_generado NO sea de este mes.
// Envuelve cada cliente en {client, __tz, __sheetUrl} para los nodos siguientes.
const { DateTime } = require('luxon');

const eligible = [];

for (const item of $input.all()) {
  const row = item.json;
  const procesar = (row.procesar || '').toString().trim()
    .toUpperCase().normalize('NFD').replace(/[\u0300-\u036f]/g, '');
  if (procesar !== 'SI') continue;

  const tz = (row.timezone || 'Europe/Madrid').toString().trim() || 'Europe/Madrid';
  const horaGenerado = (row.hora_generado || '').toString().trim();

  if (horaGenerado) {
    let last = DateTime.fromISO(horaGenerado, { zone: tz });
    if (!last.isValid) last = DateTime.fromFormat(horaGenerado, 'yyyy-MM-dd HH:mm', { zone: tz });
    if (!last.isValid) last = DateTime.fromFormat(horaGenerado, 'dd/MM/yyyy HH:mm', { zone: tz });
    if (last.isValid) {
      const nowMonth = DateTime.now().setZone(tz).toFormat('yyyy-MM');
      if (last.toFormat('yyyy-MM') === nowMonth) continue; // Ya ejecutado este mes
    }
  }

  eligible.push({
    json: {
      client: row,
      __tz: tz,
      __sheetUrl: `https://docs.google.com/spreadsheets/d/${row.client_sheet_id}/edit`
    }
  });
}

return eligible;

// Genera los 3 textos Slack a partir de las filas de CAMBIOS_KW_ORGANICAS.
// Criterios URGENTE (OR):
//   1) estado = PERDIDA
//   2) estado = BAJA + variacion <= -threshold_drop_alert + sale_top3 = TRUE
//   3) estado = BAJA + variacion <= -threshold_drop_alert + sale_top10 = TRUE
const { DateTime } = require('luxon');

// Datos del cliente (del nodo SplitInBatches)
const clientItem = $('Iterar clientes').first().json;
const client = clientItem.client;
const tz = clientItem.__tz || 'Europe/Madrid';
const sheetUrl = clientItem.__sheetUrl || '';

// Filas de CAMBIOS_KW_ORGANICAS
const rows = $input.all().map(i => i.json).filter(r => r && r.keyword);

const toBool = v => ['true','1','yes','si','s√≠','y'].includes((v??'').toString().trim().toLowerCase()) || v === true;
const toNum  = v => { const n = Number((v??'').toString().trim().replace(',','.')); return Number.isFinite(n) ? n : null; };

const threshAlert  = toNum(client.threshold_drop_alert)  ?? 3;
const threshUrgent = toNum(client.threshold_drop_urgent) ?? 10;

const changes = rows.map(r => ({
  ...r,
  variacion:  toNum(r.variacion),
  sale_top3:  toBool(r.sale_top3),
  sale_top10: toBool(r.sale_top10),
  estado: (r.estado || '').toString().trim().toUpperCase()
}));

const drops      = changes.filter(c => c.variacion !== null && c.variacion < 0);
const ups        = changes.filter(c => c.variacion !== null && c.variacion > 0);
const top3Exits  = changes.filter(c => c.sale_top3);
const top10Exits = changes.filter(c => c.sale_top10);

const urgentes = changes.filter(c => {
  if (c.estado === 'PERDIDA') return true;
  if (c.estado === 'BAJA' && c.variacion !== null && c.variacion <= -threshAlert) {
    if (c.sale_top3 || c.sale_top10) return true;
  }
  return false;
}).sort((a, b) => (a.variacion ?? 0) - (b.variacion ?? 0));

const top10Para = [...top10Exits].sort((a,b) => (a.variacion??0)-(b.variacion??0)).slice(0,5);

const fmtMove = m => m === null ? '' : (m > 0 ? `‚¨ÜÔ∏è +${m}` : m < 0 ? `‚¨áÔ∏è ${m}` : `‚Üí 0`);

const lineUrg = c => {
  const url = c.url_now || c.url_prev || '';
  const urlPart = url ? ` | <${url}|ver URL>` : '';
  if (c.estado === 'PERDIDA')
    return `‚Ä¢ *${c.keyword}* ‚Äî ‚ö†Ô∏è *PERDIDA* (antes #${c.pos_prev})${urlPart}`;
  return `‚Ä¢ *${c.keyword}* ‚Äî #${c.pos_prev} ‚Üí #${c.pos_now} (${fmtMove(c.variacion)})${urlPart}`;
};
const lineTop10 = c => {
  const url = c.url_now || c.url_prev || '';
  return `‚Ä¢ *${c.keyword}* (#${c.pos_prev} ‚Üí #${c.pos_now}) | <${url}|ver>`;
};

const dateStr   = DateTime.now().setZone(tz).toFormat('yyyy-MM-dd');
const mention   = client.slack_mention_urgent ? `${client.slack_mention_urgent} ` : '';
const urgLines  = urgentes.slice(0,10).map(lineUrg).join('\n');
const top10Lines= top10Para.map(lineTop10).join('\n');
const canalRef  = client.slack_canal_cliente_id ? `<#${client.slack_canal_cliente_id}>` : `#${(client.slack_channel_cliente||'').replace('#','')}`;

const slackTextResumenCentral =
`üìä *Resumen KW org√°nicas* (${dateStr})
üè∑Ô∏è *Cliente:* ${client.client_name||''} | üåê ${client.domain||''}
üì£ *Canal cliente:* ${canalRef}
üìå *Abrir hoja:* <${sheetUrl}|Google Sheet del cliente>

‚Ä¢ Cambios totales: *${changes.length}*
‚Ä¢ Ca√≠das: *${drops.length}* | Subidas: *${ups.length}*
‚Ä¢ Salidas Top3: *${top3Exits.length}* | Salidas Top10: *${top10Exits.length}*`;

const slackTextUrgentesCliente = (urgentes.length || top10Exits.length) ?
`${mention}üö® *KW org√°nicas ‚Äî URGENTES* (${dateStr})
üè∑Ô∏è ${client.client_name||''} | üåê ${client.domain||''}
üìå *Acceso hoja:* <${sheetUrl}|Abrir Google Sheet>

${urgentes.length ? `*üî¥ Urgentes (${urgentes.length}):*\n${urgLines}` : ''}
${top10Exits.length ? `\nüìâ *Salen del TOP10 (muestra ${top10Para.length}):*\n${top10Lines}\n_(Hay m√°s resultados en el Google Sheet)_` : ''}

üìå *Acceso r√°pido*
‚Ä¢ <${sheetUrl}|Abrir Google Sheet del cliente>
‚Ä¢ Pesta√±as: *CAMBIOS_KW_ORGANICAS* / *ALERTAS_KW_URGENTES*` : '';

return [{
  json: {
    client,
    __tz: tz,
    __sheetUrl: sheetUrl,
    slackTextResumenCentral,
    slackTextUrgentesCliente,
    urgentesCount:  urgentes.length,
    top10Count:     top10Exits.length,
  }
}];

// Busca el canal del cliente en la lista de Slack por nombre (sin #, case-insensitive).
// Si lo encuentra, devuelve su ID. Si no, devuelve null para crearlo.
const clientItem = $('Construir textos Slack (urgentes/top10/resumen)').first().json;
const canales    = $input.all().map(i => i.json).filter(c => c.id && c.name);

const norm = s => (s||'').toString().trim().replace(/^#/,'').toLowerCase().replace(/\s+/g,'-');
const desiredName = norm(clientItem.client.slack_channel_cliente || clientItem.client.slack_canal_cliente);

const encontrado = canales.find(c => norm(c.name) === desiredName);

return [{
  json: {
    ...clientItem,
    desiredChannelName:          desiredName,
    resolvedClientChannelId:     encontrado ? encontrado.id : null,
    resolvedClientChannelExists: !!encontrado,
  }
}];

// Extrae el ID del canal reci√©n creado por la API de Slack.
const prev    = $('Resolver canal cliente (buscar o crear)').first().json;
const created = $input.first().json;
const newId   = created?.id || created?.channel?.id || null;

return [{
  json: {
    ...prev,
    resolvedClientChannelId:     newId,
    resolvedClientChannelExists: !!newId,
  }
}];
